using System.Collections.Generic;
using System.Diagnostics.CodeAnalysis;
using System.Linq;
using System.Net;
using System.Net.Http;
using System.Net.Http.Headers;

public static class HttpRequestMessageExtensions
{
    public static readonly string HeaderClientRequestId = "x-ms-client-request-id";

    [SuppressMessage("Microsoft.Design", "CA1062:Validate arguments of public methods", Justification = "By design. As this is a extension method caller has to take care of it.")]
    public static string GetClientRequestId(HttpRequestMessage request)
    {
        return HttpRequestMessageExtensions.GetFirstOrDefaultHeader(request: request, name: HttpRequestMessageExtensions.HeaderClientRequestId, defaultValue: string.Empty);
    }

    [SuppressMessage("Microsoft.Design", "CA1026:DefaultParametersShouldNotBeUsed", Justification = "By design")]
    [SuppressMessage("Microsoft.Design", "CA1062:Validate arguments of public methods", Justification = "By design. As this is a extension method caller has to take care of it.")]
    public static string GetFirstOrDefaultHeader(HttpRequestMessage request, string name, string defaultValue = null)
    {
        IEnumerable<string> values = (IEnumerable<string>)null;

        if (request.Headers.TryGetValues(name, out values) && values.Any<string>())
        {
            return values.First<string>();
        }

        return defaultValue;
    }

    [SuppressMessage("Microsoft.Design", "CA1062:Validate arguments of public methods", Justification = "By design. As this is a extension method caller has to take care of it.")]
    public static HttpResponseMessage CreateXmlResponse(HttpRequestMessage request, HttpStatusCode statusCode, HttpContent content)
    {
        var response = request.CreateResponse(statusCode);

        try
        {
            if (content != null)
            {
                response.Content = content;
                response.Content.Headers.ContentType = new MediaTypeHeaderValue("application/xml");
            }
        }
        catch
        {
            response.Dispose();
            response = null;
            throw;
        }

        return response;
    }
}

files.run.csx:
#r "Newtonsoft.Json"
#r "System.Xml.Linq"
#load "XsltHelper.csx"
#load "HttpRequestMessageExtensions.csx"
#load "IntegrationXsltException.csx"

using System;
using System.Collections.Generic;
using System.Globalization;
using System.IO;
using System.Linq;
using System.Net;
using System.Net.Http;
using System.Net.Http.Headers;
using System.Text;
using System.Threading;
using System.Threading.Tasks;
using System.Xml;
using System.Xml.Linq;
using System.Xml.Xsl;
using Newtonsoft.Json;
using Newtonsoft.Json.Linq;

public static async Task<HttpResponseMessage> Run(HttpRequestMessage req, TraceWriter log, CancellationToken token)
{
    var clientRequestId = string.Empty;

    try
    {
        clientRequestId = HttpRequestMessageExtensions.GetClientRequestId(request: req);
        log.Info(string.Format("Executing transform operation. client request id: '{0}'", clientRequestId));
        return await XsltHelper.Transform(req, token).ConfigureAwait(continueOnCapturedContext: false);
    }
    catch (IntegrationXsltException ex)
    {
        log.Info(string.Format("An exception occurred while performing transform. client request id: '{0}' and exception: '{1}'", clientRequestId, ex.ToString()));
        return req.CreateErrorResponse(ex.StatusCode, ex.Message);
    }
    catch (Exception ex)
    {
        log.Info(string.Format("An exception occurred while performing transform. client request id: '{0}' and exception: '{1}'", clientRequestId, ex.ToString()));
        throw;
    }
}

