| where isempty(_BackupInstanceName) or _BackupInstanceName == "*" or  BackupItemFriendlyName contains (_BackupInstanceName)
| extend BackupItemProtectionState = iff(BackupItemProtectionState in ("Protected", "ActivelyProtected","ProtectionError"), "Protected", iff(BackupItemProtectionState in ("IRPending"), "InitialBackupPending", iff(isnotempty(BackupItemProtectionState),"ProtectionStopped",BackupItemProtectionState)))
//| where BackupItemProtectionState in~ (_ProtectionStateList) or '*' in (_ProtectionStateList)
| project BackupItemUniqueId,  BackupItemName, BackupItemFriendlyName, BackupManagementType, BackupItemType, DatasourceType, BackupItemProtectionState,
StorageReplicationType, ResourceId, VaultUniqueId, VaultName, VaultTags, SubscriptionId, ResourceGroupName, AzureDataCenter};
let BI_CombinationUnderAzureDiagnostics = ()
{
let JoinWithPolicy = (T:(PolicyUniqueId:string, ResourceId:string))
{
T | join kind= leftouter (
 PolicyUnderAzureDiagnostics | project PolicyUniqueId, PolicyName, ResourceId) on PolicyUniqueId, ResourceId
};
let Base = () {ProtectedContainerUnderAzureDiagnostics | distinct ProtectedContainerName, ProtectedContainerFriendlyName, ProtectedContainerUniqueId 
| join kind= rightouter  (
    BackupItemAssociationUnderAzureDiagnostics 
	// To show as per as on 'AsonDay'
	| where startofday(TimeGenerated) == AsonDay
	| project ProtectedContainerUniqueId, BackupItemUniqueId, BackupManagementServerUniqueId, PolicyUniqueId, TimeGenerated, ResourceId
) on ProtectedContainerUniqueId 
| project BackupItemUniqueId, ProtectedContainerUniqueId = ProtectedContainerUniqueId1, ProtectedContainerName, ProtectedContainerFriendlyName, BackupManagementServerUniqueId, PolicyUniqueId, TimeGenerated, ResourceId
};
let Base_Policy = ()
{
JoinWithPolicy(Base) 
| project BackupItemUniqueId, ProtectedContainerUniqueId, ProtectedContainerName, ProtectedContainerFriendlyName, BackupManagementServerUniqueId, PolicyUniqueId, PolicyName, TimeGenerated, ResourceId
};
Base_Policy
};
let BI_CombinationUnderResourceSpecific = ()
{
let JoinWithPolicy = (T:(PolicyUniqueId:string, ResourceId:string))
{
T | join kind= leftouter (
 PolicyUnderResourceSpecific | project PolicyUniqueId, PolicyName, ResourceId) on PolicyUniqueId, ResourceId
};
let Base = () {ProtectedContainerUnderResourceSpecific | distinct ProtectedContainerName, ProtectedContainerFriendlyName, ProtectedContainerUniqueId 
| join kind= rightouter  (
    BackupItemAssociationUnderResourceSpecific 
	// To show as per as on 'AsonDay'
	| where startofday(TimeGenerated) == AsonDay
	| project ProtectedContainerUniqueId, BackupItemUniqueId, BackupManagementServerUniqueId, PolicyUniqueId, TimeGenerated, ResourceId
) on ProtectedContainerUniqueId 
| project BackupItemUniqueId, ProtectedContainerUniqueId = ProtectedContainerUniqueId1, ProtectedContainerName, ProtectedContainerFriendlyName, BackupManagementServerUniqueId, PolicyUniqueId, TimeGenerated, ResourceId
};
let Base_Policy = ()
{
JoinWithPolicy(Base) 
| project BackupItemUniqueId, ProtectedContainerUniqueId, ProtectedContainerName, ProtectedContainerFriendlyName, BackupManagementServerUniqueId, PolicyUniqueId, PolicyName, TimeGenerated, ResourceId
};
Base_Policy
};
let LatestBackupItemWithProtectedContainerTable = () 
{
TotalBackupItemDimensionTable
| join  (union isfuzzy = true   
(BI_CombinationUnderAzureDiagnostics()| where _IncludeLegacyEvent == true),
(BI_CombinationUnderResourceSpecific())
| summarize arg_max(TimeGenerated, *) by BackupItemUniqueId) on BackupItemUniqueId
| where isempty(_DatasourceSetName) or _DatasourceSetName == "*" or ProtectedContainerFriendlyName contains (_DatasourceSetName)
| project BackupItemUniqueId,  BackupItemName, BackupItemFriendlyName, BackupManagementType, BackupItemType, DatasourceType, BackupItemProtectionState,
StorageReplicationType, ResourceId, VaultUniqueId, VaultName, VaultTags, SubscriptionId, ResourceGroupName, AzureDataCenter, ProtectedContainerUniqueId, ProtectedContainerName, ProtectedContainerFriendlyName, BackupManagementServerUniqueId, PolicyUniqueId, PolicyName, TimeGenerated
};
let JobFinalTable = (){
 union isfuzzy = true 
(JobUnderAzureDiagnostics(true)| where _IncludeLegacyEvent == true),
(JobUnderResourceSpecific(true))
| project JobStatus, JobUniqueId, JobOperation, JobFailureCode, JobOperationSubType, JobStartDateTime, DataTransferredInMB, AdHocOrScheduledJob, JobDurationInSecs, RecoveryJobDestination,
RecoveryJobRPDateTime, RecoveryJobRPLocation, RecoveryLocationType, BackupItemUniqueId, ProtectedContainerUniqueId, TimeGenerated 
| summarize arg_max(TimeGenerated, *)  by BackupItemUniqueId, JobUniqueId
| where JobOperation in~ (_JobOperationList) or '*' in (_JobOperationList)
| where JobStatus in~ (_JobStatusList) or '*' in (_JobStatusList)
| where JobFailureCode in~ (_JobFailureCodeList) or '*' in (_JobFailureCodeList)
| join kind= inner (LatestBackupItemWithProtectedContainerTable) on BackupItemUniqueId
| project JobUniqueId, JobOperation, JobStatus, JobFailureCode, JobOperationSubType, JobStartDateTime, JobDurationInSecs, DataTransferredInMB, AdHocOrScheduledJob, RecoveryJobDestination,
RecoveryJobRPDateTime, RecoveryJobRPLocation, RecoveryLocationType, BackupItemUniqueId = BackupItemUniqueId1, BackupItemName, BackupItemFriendlyName, BackupManagementType, BackupItemType, DatasourceType, BackupItemProtectionState, StorageReplicationType, ResourceId, VaultUniqueId, VaultName, VaultTags, SubscriptionId, ResourceGroupName, AzureDataCenter, ProtectedContainerUniqueId, ProtectedContainerName, ProtectedContainerFriendlyName,  PolicyUniqueId, PolicyName, TimeGenerated
};
let FinalTable_V1Vault = () {Extend_DatasourceARMType(Extend_BackupInstanceId(Extend_DatasourceSetResourceId_DatasourceSetType_DatasourceResourceId(JobFinalTable)))
| project UniqueId = JobUniqueId, OperationCategory = JobOperation, Operation = JobOperationSubType, Status = JobStatus, ErrorTitle = JobFailureCode, StartTime = JobStartDateTime, Duration_In_Secs = JobDurationInSecs, DataTransferredInMBs = DataTransferredInMB, RestoreJobRPDateTime = RecoveryJobRPDateTime, RestoreJobRPLocation = RecoveryJobRPLocation, BackupInstanceUniqueId = BackupItemUniqueId, BackupInstanceId, BackupInstanceFriendlyName = BackupItemFriendlyName, DatasourceResourceId, DatasourceFriendlyName = BackupItemFriendlyName, DatasourceARMType, DatasourceType,  DatasourceSetResourceId, DatasourceSetType, DatasourceSetFriendlyName = ProtectedContainerFriendlyName, VaultResourceId = ResourceId, VaultUniqueId, VaultName, VaultTags, VaultSubscriptionId = SubscriptionId, VaultLocation = AzureDataCenter, VaultStore_StorageReplicationType = StorageReplicationType,   VaultType = "Microsoft.RecoveryServices/vaults", TimeGenerated};
FinalTable_V1Vault 
| where "Microsoft.RecoveryServices/vaults" in~ (_VaultTypeList) or '*' in (_VaultTypeList)

