let BackupInstanceFunctionRedirect = (){
@{variables('BackupInstanceFunction')}
};
let BackupItemWithoutSuccessfulBackup = (){
// calling LA System Function
@{variables('JobFunction')}
| where OperationCategory has "Backup"
| where Status in ("Completed","CompletedWithWarnings")
| join kind= rightanti (
BackupInstanceFunctionRedirect 
) on $left.BackupInstanceUniqueId == $right.UniqueId
| project UniqueId,  BackupInstanceFriendlyName = FriendlyName,  DatasourceType, ProtectionStatus, VaultResourceId
};
let BackupItemWithoutSuccessfulBackupAndItsRecoveryPointTable = ()
{
BackupItemWithoutSuccessfulBackup | join 
(
BackupInstanceFunctionRedirect 
) on UniqueId
| where not(LatestRecoveryPoint >= datetime(@{parameters('RangeStart')})  and LatestRecoveryPoint <= datetime(@{parameters('RangeEnd')}) )
};
BackupItemWithoutSuccessfulBackupAndItsRecoveryPointTable
| project BackupInstance=BackupInstanceFriendlyName, Id, Container=DatasourceSetFriendlyName, ResourceGroup=iff(DatasourceType=="Azure Backup Agent" or DatasourceType=="Azure Backup Server" or DatasourceType=="DPM","(none)",split(split(tostring(tolower(DatasourceSetResourceId)), '/resourcegroups/')[1],'/')[0]
), Policy=PolicyName, LatestRecoveryPoint, Vault=VaultResourceId, AzureResource=DatasourceSetResourceId, DatasourceType

let PolicyFunctionRedirect = ()
{
@{variables('PolicyFunction')}
| extend ExtendedPropertiesJson = parse_json(ExtendedProperties)
};
let BackupInstanceFunctionRedirect = ()
{
@{variables('BackupInstanceFunction')}
};
let OutputTable = () {union isfuzzy = true 
BackupInstanceFunctionRedirect
| join kind = leftouter (
PolicyFunctionRedirect
) on $left.PolicyUniqueId == $right.UniqueId, $left.VaultResourceId == $right.VaultResourceId
};
OutputTable
// query to transform function output
| extend DailyRPRetentionDuration = ExtendedPropertiesJson.DailyRetentionDuration, WeeklyRPRetentionDuration = ExtendedPropertiesJson.WeeklyRetentionDuration, MonthlyRPRetentionDuration = ExtendedPropertiesJson.MonthlyRetentionDuration, YearlyRPRetentionDuration = ExtendedPropertiesJson.YearlyRetentionDuration
| where  (DailyRPRetentionDuration > @{parameters('DailyRetentionParam')}) and (WeeklyRPRetentionDuration > @{parameters('WeeklyRetentionParam')}) and (MonthlyRPRetentionDuration > @{parameters('MonthlyRetentionParam')}) and (YearlyRPRetentionDuration > @{parameters('YearlyRetentionParam')})
| project Id, BackupInstance=FriendlyName, Container=DatasourceSetFriendlyName, ResourceGroup=iff(DatasourceType=="Azure Backup Agent" or DatasourceType=="Azure Backup Server" or DatasourceType=="DPM","(none)",split(split(tostring(tolower(DatasourceSetResourceId)), '/resourcegroups/')[1],'/')[0]
), Policy=Name, DailyRPRetentionDuration, WeeklyRPRetentionDuration, MonthlyRPRetentionDuration, YearlyRPRetentionDuration, Vault=VaultResourceId, AzureResource=DatasourceSetResourceId, DatasourceType

