@{variables('WorkspaceList')}
// Report filters
let _RangeStart = datetime(@{parameters('RangeStart')}) ;
let _RangeEnd = datetime(@{parameters('RangeEnd')}) ;
let _VaultSubscriptionList = split(@{parameters('VaultSubscriptionList')}, ',');
let _VaultLocationList = split(@{parameters('VaultLocationList')}, ',');
let _VaultList = split(@{parameters('VaultList')}, ',');
let _VaultTypeList = "*";
let _IncludeLegacyEvent= not(@{parameters('ExcludeLegacyEvent')});
let _DatasourceTypeList =  split(@{parameters('DatasourceTypeList')}, ',');
let _ProtectionStateList = "*";
let _DatasourceSetName = "*";
let _BackupInstanceName = "*";
let _JobOperationList = "*";
let _JobStatusList = "*";
let _JobFailureCodeList = "*";
let _IncludeLog = false;
// Other Vars
let ExtRangeStart = _RangeStart - 2d;
let ExtRangeEnd = _RangeEnd + 2d;
let AsonDay = iff((_RangeEnd-startofday(now()) == 1d),_RangeEnd-2d, _RangeEnd-1d);
let AzureStorageCutoffDate = datetime(6/01/2020, 12:00:00.000 AM);
// HelperFunctions
let Extend_DatasourceType = (T:(BackupManagementType:string, BackupItemType:string))
{
T | extend DatasourceType = iff(BackupManagementType == "IaaSVM", "Azure Virtual Machine", 
iff(BackupManagementType == "MAB", "Azure Backup Agent", 
iff(BackupManagementType == "DPM", "DPM", 
iff(BackupManagementType == "AzureBackupServer", "Azure Backup Server", 
iff(BackupManagementType == "AzureStorage", "Azure Storage (Azure Files)", 
iff(BackupManagementType == "AzureWorkload" and BackupItemType == "SQLDataBase", "SQL in Azure VM", 
iff(BackupManagementType == "AzureWorkload" and BackupItemType == "SAPHanaDatabase", "SAP HANA in Azure VM", "")))))))
};
let Extend_DatasourceARMType = (T:(BackupManagementType:string, BackupItemType:string))
{
T | extend DatasourceARMType = iff(BackupManagementType == "IaaSVM", "Microsoft.Compute/virtualMachines", 
iff(BackupManagementType == "MAB", strcat("Azure Backup Agent", ";", BackupItemType), 
iff(BackupManagementType == "DPM", strcat("DPM", ";", BackupItemType), 
iff(BackupManagementType == "AzureBackupServer", strcat("Azure Backup Server", ";", BackupItemType), 
iff(BackupManagementType == "AzureStorage", "Microsoft.Storage/storageAccounts/fileServices/shares", 
iff(BackupManagementType == "AzureWorkload" and BackupItemType == "SQLDataBase", "SQLDataBase", 
iff(BackupManagementType == "AzureWorkload" and BackupItemType == "SAPHanaDatabase", "SAPHanaDatabase", "")))))))
};
let Extend_BackupInstanceId = (T:(ResourceId:string, BackupManagementType:string, BackupItemType:string, ProtectedContainerName:string, BackupItemName:string))
{
T | extend BackupInstanceId =  toupper(iff ((BackupManagementType == "IaaSVM" and BackupItemType == "VM"), strcat(ResourceId,"/backupFabrics/Azure/protectionContainers/IaasVMContainer;", ProtectedContainerName, "/protectedItems/VM;", ProtectedContainerName),
iff((BackupManagementType == "AzureStorage" and BackupItemType == "AzureFileShare"), strcat(ResourceId,"/backupFabrics/Azure/protectionContainers/StorageContainer;", ProtectedContainerName, "/protectedItems/AzureFileShare;", BackupItemName) , 
iff((BackupManagementType == "AzureWorkload" and BackupItemType == "SQLDataBase"), strcat(ResourceId,"/backupFabrics/Azure/protectionContainers/VMAppContainer;", ProtectedContainerName, "/protectedItems/SQLDataBase;", BackupItemName) , 
iff((BackupManagementType == "AzureWorkload" and BackupItemType == "SAPHanaDatabase"), strcat(ResourceId,"/backupFabrics/Azure/protectionContainers/VMAppContainer;", ProtectedContainerName, "/protectedItems/SAPHanaDatabase;", BackupItemName), "")))))
};
let Extend_DatasourceSetResourceId_DatasourceSetType_DatasourceResourceId = (T:(ResourceId:string, ProtectedContainerName:string, BackupManagementType:string, BackupItemType:string, BackupItemUniqueId:string, BackupItemName:string, BackupItemFriendlyName:string))
{
T | extend prefix = array_strcat(array_split(split(ResourceId,"/"), 4)[0] ,"/")
|  extend container_array = split(ProtectedContainerName,";")
|  extend container_arraylen = array_length(container_array)
| extend containerNameString = iff(container_arraylen == 3, ProtectedContainerName, "")
| parse containerNameString with entityType:string ";" rgName:string ";" entityName:string
| extend entityURL = iff((BackupManagementType == "AzureStorage" and BackupItemType == "AzureFileShare"), iff(entityType == "storage", "/Microsoft.Storage/storageAccounts/", "/Microsoft.ClassicStorage/storageAccounts/"), iff((BackupManagementType == "IaaSVM" and BackupItemType == "VM"), iff(entityType =~ "iaasvmcontainerv2", "/Microsoft.Compute/virtualMachines/", "/Microsoft.ClassicCompute/virtualMachines/"), iff(((BackupManagementType == "AzureWorkload" and BackupItemType == "SAPHanaDatabase") or (BackupManagementType == "AzureWorkload" and BackupItemType == "SQLDataBase")), iff(entityType =~ "compute", "/Microsoft.Compute/virtualMachines/", "/Microsoft.ClassicCompute/virtualMachines/"), "")))
| extend DatasourceSetResourceId = toupper(iff(BackupManagementType in ("DPM", "AzureBackupServer", "MAB"), "" , iff(containerNameString != "", strcat(prefix, "/", rgName, "/providers", entityURL, entityName), ResourceId)))
//DatasourceType
| extend DatasourceSetType = iff(BackupManagementType == "IaaSVM", iff(entityType =~ "iaasvmcontainerv2", "Microsoft.Compute/virtualMachines", "Microsoft.ClassicCompute/virtualMachines"),  
iff(BackupManagementType == "MAB", "Azure Backup Agent", 
iff(BackupManagementType == "DPM", "DPM", 
iff(BackupManagementType == "AzureBackupServer", "Azure Backup Server", 
iff(BackupManagementType == "AzureStorage", iff(entityType == "storage", "Microsoft.Storage/storageAccounts", "Microsoft.ClassicStorage/storageAccounts"), 
iff(BackupManagementType == "AzureWorkload" and BackupItemType == "SQLDataBase", iff(entityType =~ "compute", "Microsoft.Compute/virtualMachines", "Microsoft.ClassicCompute/virtualMachines"), 
iff(BackupManagementType == "AzureWorkload" and BackupItemType == "SAPHanaDatabase", iff(entityType =~ "compute", "Microsoft.Compute/virtualMachines", "Microsoft.ClassicCompute/virtualMachines"), "")))))))
| extend DatasourceResourceId = toupper(iff(BackupManagementType in ("DPM", "AzureBackupServer", "MAB"), BackupItemUniqueId, 
iff(BackupManagementType == "IaaSVM", DatasourceSetResourceId, 
iff(BackupManagementType == "AzureStorage", strcat(DatasourceSetResourceId, "/fileServices/default/shares/", BackupItemFriendlyName),
iff(BackupManagementType == "AzureWorkload" and BackupItemType == "SQLDataBase",strcat(DatasourceSetResourceId, "/providers/Microsoft.RecoveryServices/backupProtectedItem/SQLDataBase;", BackupItemName),
iff(BackupManagementType == "AzureWorkload" and BackupItemType == "SAPHanaDatabase",strcat(DatasourceSetResourceId, "/providers/Microsoft.RecoveryServices/backupProtectedItem/SAPHanaDatabase;", BackupItemName),""))))))
| project-away prefix, container_array, container_arraylen, containerNameString, entityURL 
};
// Source Tables
let VaultUnderAzureDiagnostics = ()
{
AzureDiagnostics
// Take records until previous day
| where TimeGenerated >= ExtRangeStart and TimeGenerated <= _RangeEnd and TimeGenerated < startofday(now())
| where Category == "AzureBackupReport" and OperationName == "Vault" and columnifexists("SchemaVersion_s", "") == "V2"
| project VaultName = columnifexists("VaultName_s", ""), VaultUniqueId = columnifexists("VaultUniqueId_s", ""), VaultTags = columnifexists("VaultTags_s", ""), AzureDataCenter =  columnifexists("AzureDataCenter_s", ""), ResourceGroupName =  columnifexists("ResourceGroupName_s", ""), SubscriptionId = toupper(SubscriptionId), StorageReplicationType = StorageReplicationType_s, ResourceId, TimeGenerated 
| where SubscriptionId in~ (_VaultSubscriptionList) or '*' in (_VaultSubscriptionList)
| where AzureDataCenter in~ (_VaultLocationList) or '*' in (_VaultLocationList)
| where VaultName in~  (_VaultList) or '*' in (_VaultList)
| summarize arg_max(TimeGenerated, *) by ResourceId
| project StorageReplicationType, VaultUniqueId, VaultName, VaultTags, SubscriptionId, ResourceGroupName, AzureDataCenter, ResourceId, TimeGenerated
};
let VaultUnderResourceSpecific = ()
{
CoreAzureBackup
// Take records until previous day
| where TimeGenerated >= ExtRangeStart and TimeGenerated <= _RangeEnd and TimeGenerated < startofday(now())
| where OperationName == "Vault" 
| project StorageReplicationType, VaultUniqueId, VaultName, VaultTags, SubscriptionId = toupper(SubscriptionId), ResourceGroupName, AzureDataCenter, ResourceId, TimeGenerated 
| where SubscriptionId in~ (_VaultSubscriptionList) or '*' in (_VaultSubscriptionList)
| where AzureDataCenter in~ (_VaultLocationList) or '*' in (_VaultLocationList)
| where VaultName in~  (_VaultList) or '*' in (_VaultList)
| summarize arg_max(TimeGenerated, *) by ResourceId
};
