let BackupItemUnderAzureDiagnostics = ()
{
let SourceBackupItemTable = AzureDiagnostics
// Take records until previous day
| where TimeGenerated >= _RangeStart and TimeGenerated <= _RangeEnd and TimeGenerated < startofday(now())
| where Category == "AzureBackupReport" and OperationName == "BackupItem" and columnifexists("SchemaVersion_s", "") == "V2" and columnifexists("State_s", "") != "Deleted"
| project BackupItemUniqueId = columnifexists("BackupItemUniqueId_s", ""), BackupItemProtectionState = columnifexists("BackupItemProtectionState_s", ""), BackupItemAppVersion = columnifexists("BackupItemAppVersion_s", ""),SecondaryBackupProtectionState = columnifexists("SecondaryBackupProtectionState_s", ""), BackupItemName = columnifexists("BackupItemName_s", ""), BackupItemFriendlyName = columnifexists("BackupItemFriendlyName_s", ""),
BackupItemType = columnifexists("BackupItemType_s", ""),  ProtectionGroupName = columnifexists("ProtectionGroupName_s", ""), BackupManagementType = columnifexists("BackupManagementType_s", ""), TimeGenerated, ResourceId
//Handle MAB system state
// Excluding SecondaryBackupProtectionState, BackupItemAppVersion, ProtectionGroupName
|  project BackupItemUniqueId = iff((BackupManagementType == "MAB" and BackupItemUniqueId contains "ssbv\\"), replace(@"[^;]+$", @"systemstate", BackupItemUniqueId ), BackupItemUniqueId), BackupItemName = iff((BackupManagementType == "MAB" and BackupItemUniqueId contains "ssbv\\"), "System State", BackupItemName), BackupItemProtectionState, BackupItemAppVersion, SecondaryBackupProtectionState, ProtectionGroupName, BackupItemFriendlyName, BackupItemType, BackupManagementType, TimeGenerated, ResourceId
