| where BackupSolution in~ (_BackupSolutionList) or '*' in (_BackupSolutionList)
| summarize arg_max(TimeGenerated, *) by BackupItemUniqueId;
VaultUnderAzureDiagnostics | join   (
   BackupItemTable 
) on ResourceId
| project-away ResourceId1, TimeGenerated1;
};
