inputs.body:
let RangeStart = startofday(datetime(@{parameters('startDate')}));
let RangeEnd = startofday(datetime(@{parameters('endDate')}));
let aggregationTypeParam = @{parameters('aggregationType')};
let BackupInstanceRedirect = ()
{
// calling LA System Function
@{variables('BackupInstanceFunction')}
};
let PolicyFunctionRedirect = ()
{
@{variables('PolicyFunction')}
};
let JobFunctionRedirect = ()
{
@{variables('JobFunction')}
| where OperationCategory == "Backup"
};
// Grid specific params
let bin_Value = iff(aggregationTypeParam == "Weekly", 7d, iff(aggregationTypeParam == "Monthly", 28d, 1d));
let sum_isJobSuccessInTheTimePeriod_Value = iff(aggregationTypeParam == "Weekly", "7", iff(aggregationTypeParam == "Monthly", "28", "1")); 
let duration_Value = iff(aggregationTypeParam == "Weekly", "6.00:00:00", iff(aggregationTypeParam == "Monthly", "27.00:00:00", "00.00:00:00")); 
let BackupInstanceWithDailyBackup = (){
BackupInstanceRedirect
| join kind = inner (
// calling LA System Function
PolicyFunctionRedirect
) on $left.PolicyUniqueId == $right.UniqueId, $left.VaultResourceId == $right.VaultResourceId
| where parse_json(ExtendedProperties).BackupFrequency == "Daily"
};
// Grid Logic
let JobInfo = ()
{
JobFunctionRedirect
| join kind = inner (BackupInstanceWithDailyBackup) on $left.BackupInstanceUniqueId == $right.UniqueId
| summarize jobCount = count(), jobSuccessCount = countif(Status == "Completed" or Status == "CompletedWithWarnings"), BackupInstanceFriendlyName = any(BackupInstanceFriendlyName), BackupInstanceId=any(BackupInstanceId), BackupSolution =  any(BackupSolution), DatasourceSetResourceId = any(DatasourceSetResourceId), DatasourceSetFriendlyName = any(DatasourceSetFriendlyName) by BackupInstanceUniqueId, JobCreationDate = startofday(StartTime)
| project ResourceGroup=iff(BackupSolution =="Azure Backup Agent" or BackupSolution =="Azure Backup Server" or BackupSolution =="DPM","(none)",split(split(tostring(tolower(DatasourceSetResourceId)), '/resourcegroups/')[1],'/')[0]), jobCount, jobSuccessCount, BackupInstanceFriendlyName, BackupInstanceUniqueId, DatasourceSetFriendlyName, JobCreationDate, BackupInstanceId
};
JobInfo
| summarize by BackupInstanceUniqueId, BackupInstanceFriendlyName, DatasourceSetFriendlyName, ResourceGroup, BackupInstanceId
| extend Day = range(RangeEnd, RangeStart, -1d)
| mv-expand Day
| project BackupInstanceUniqueId, BackupInstanceFriendlyName, BackupInstanceId, DatasourceSetFriendlyName, ResourceGroup, todatetime(Day)
| join kind = leftouter 
( JobInfo
) on BackupInstanceUniqueId, $left.Day == $right.JobCreationDate
| sort by BackupInstanceUniqueId, Day desc
| project BackupInstanceUniqueId, BackupInstanceId, BackupInstanceFriendlyName, DatasourceSetFriendlyName, ResourceGroup, Day, jobTotalCountInTheTimePeriod = iff(isnull(jobCount), 0, jobCount), isJobSuccessInTheTimePeriod =iff(isnull(jobCount), 0, iff((jobSuccessCount > 1), 1, jobSuccessCount))
| summarize max(Day), min(Day), sum(isJobSuccessInTheTimePeriod), BackupInstanceFriendlyName = any(BackupInstanceFriendlyName),BackupInstanceId = any(BackupInstanceId), DatasourceSetFriendlyName = any(DatasourceSetFriendlyName), ResourceGroup = any(ResourceGroup) by BackupInstanceUniqueId, bin_at(Day, bin_Value, RangeEnd + 1d)
| project BackupInstanceUniqueId, BackupInstanceId, BackupInstanceFriendlyName, DatasourceSetFriendlyName, ResourceGroup, Period = iff(aggregationTypeParam == "Weekly" or aggregationTypeParam == "Monthly", strcat(format_datetime(todatetime(min_Day),"yyyy/MM/dd"), " - ", format_datetime(todatetime(max_Day),"yyyy/MM/dd")), format_datetime(todatetime(min_Day),"yyyy/MM/dd")), sum_isJobSuccessInTheTimePeriod, duration = max_Day - min_Day, Day
// Exclude partial week/month
| where duration == duration_Value
| summarize sum_isJobSuccessInTheTimePeriod = countif(sum_isJobSuccessInTheTimePeriod == sum_isJobSuccessInTheTimePeriod_Value), Day= (any(Day)), BackupInstanceFriendlyName = any(BackupInstanceFriendlyName), BackupInstanceId = any(BackupInstanceId), DatasourceSetFriendlyName = any(DatasourceSetFriendlyName), ResourceGroup = any(ResourceGroup) by BackupInstanceUniqueId, Period
| evaluate pivot(Period, any(sum_isJobSuccessInTheTimePeriod), BackupInstanceFriendlyName, BackupInstanceId, BackupInstanceUniqueId, DatasourceSetFriendlyName, ResourceGroup)
| sort by BackupInstanceUniqueId asc

