search.query:
let T1 = CF_ValueMetric_CL | where Origin_s == "bbs" and Name_s == "LRPsRunning" and EventTime_t > ago(1h) | summarize CurrentHourAverage = avg(Value_d) by Environment_s;
let T2 = CF_ValueMetric_CL | where Origin_s == "bbs" and Name_s == "LRPsRunning" and EventTime_t > ago(2h) and EventTime_t < ago(1h) | summarize PreviousHourAverage = avg(Value_d) by Environment_s;
 T1 | join (T2) on Environment_s | where abs(CurrentHourAverage-PreviousHourAverage) > 20

let T1 = CF_ValueMetric_CL | where Origin_s == "bbs" and Name_s == "LRPsRunning" and EventTime_t > ago(1h) | summarize CurrentHourAverage = avg(Value_d) by Environment_s;
let T2 = CF_ValueMetric_CL | where Origin_s == "bbs" and Name_s == "LRPsRunning" and EventTime_t > ago(2h) and EventTime_t < ago(1h) | summarize PreviousHourAverage = avg(Value_d) by Environment_s;
 T1 | join (T2) on Environment_s | where abs(CurrentHourAverage-PreviousHourAverage) > 10

let T1 = CF_ValueMetric_CL | where Origin_s == "gorouter" and Name_s == "total_routes" | summarize TotalRoutes = avg(Value_d) by Environment_s, bin(EventTime_t, 5m);
let T2 = T1 | project Environment_s, EventTime_t = EventTime_t-5m, TotalRoutesPrevious = TotalRoutes;
T1 | join T2 on Environment_s, EventTime_t | project Environment_s, EventTime_t, TotalRoutes, TotalRoutesPrevious, TotalRoutesDelta = TotalRoutesPrevious - TotalRoutes | where TotalRoutesDelta > 20

let T1 = CF_ValueMetric_CL | where Origin_s == "gorouter" and Name_s == "total_routes" | summarize TotalRoutes = avg(Value_d) by Environment_s, bin(EventTime_t, 5m);
let T2 = T1 | project Environment_s, EventTime_t = EventTime_t-5m, TotalRoutesPrevious = TotalRoutes;
T1 | join T2 on Environment_s, EventTime_t | project Environment_s, EventTime_t, TotalRoutes, TotalRoutesPrevious, TotalRoutesDelta = TotalRoutesPrevious - TotalRoutes | where TotalRoutesDelta > 10

let T1 = CF_CounterEvent_CL | where Origin_s == "gorouter" and Name_s == "registry_message.route-emitter" | summarize RegistryMessage = sum(Delta_d) by Environment_s;
let T2 = CF_CounterEvent_CL | where Origin_s == "route_emitter" and Name_s == "MessagesEmitted" | summarize MessagesEmitted = sum(Delta_d) by Environment_s;
T1 | join T2 on Environment_s | project Environment_s, RegistryMessage, MessagesEmitted, MessageLost = RegistryMessage - MessagesEmitted | where MessageLost > 20

let T1 = CF_CounterEvent_CL | where Origin_s == "gorouter" and Name_s == "registry_message.route-emitter" | summarize RegistryMessage = sum(Delta_d) by Environment_s;
let T2 = CF_CounterEvent_CL | where Origin_s == "route_emitter" and Name_s == "MessagesEmitted" | summarize MessagesEmitted = sum(Delta_d) by Environment_s;
T1 | join T2 on Environment_s | project Environment_s, RegistryMessage, MessagesEmitted, MessageLost = RegistryMessage - MessagesEmitted | where MessageLost > 0
